import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { theme } from "../styles/theme";
import { motion, AnimatePresence } from "framer-motion";

interface CommandItem {
    id: string;
    label: string;
    category: "page" | "project" | "workspace" | "action";
    icon: string;
    action: () => void;
    keywords?: string[];
}

interface CommandPaletteProps {
    isOpen: boolean;
    onClose: () => void;
}

export default function CommandPalette({ isOpen, onClose }: CommandPaletteProps) {
    const [query, setQuery] = useState("");
    const [selectedIndex, setSelectedIndex] = useState(0);
    const navigate = useNavigate();

    const commands: CommandItem[] = [
        // Pages
        { id: "home", label: "Home", category: "page", icon: "­ƒÅá", action: () => navigate("/") },
        { id: "chat", label: "Chat", category: "page", icon: "­ƒÆ¼", action: () => navigate("/chat") },
        { id: "search", label: "B├║squeda", category: "page", icon: "­ƒöì", action: () => navigate("/search") },
        { id: "workspaces", label: "Workspaces", category: "page", icon: "­ƒÅó", action: () => navigate("/workspaces") },
        { id: "projects", label: "Proyectos", category: "page", icon: "­ƒôü", action: () => navigate("/projects") },
        { id: "settings", label: "Configuraci├│n", category: "page", icon: "ÔÜÖ´©Å", action: () => navigate("/settings") },
        { id: "debug", label: "Debug Panel", category: "page", icon: "­ƒÉø", action: () => navigate("/debug"), keywords: ["logs", "agent"] },
        { id: "admin", label: "Admin Panel", category: "page", icon: "­ƒæ¿ÔÇì­ƒÆ╝", action: () => navigate("/admin") },

        // Actions
        { id: "new-chat", label: "Nueva Conversaci├│n", category: "action", icon: "Ô×ò", action: () => navigate("/chat") },
        { id: "new-workspace", label: "Nuevo Workspace", category: "action", icon: "­ƒôé", action: () => console.log("Create workspace") },
        { id: "new-project", label: "Nuevo Proyecto", category: "action", icon: "­ƒôä", action: () => navigate("/projects/new") },
    ];

    const filteredCommands = commands.filter((cmd) => {
        const searchText = query.toLowerCase();
        return (
            cmd.label.toLowerCase().includes(searchText) ||
            cmd.category.toLowerCase().includes(searchText) ||
            cmd.keywords?.some((k) => k.toLowerCase().includes(searchText))
        );
    });

    useEffect(() => {
        if (!isOpen) {
            setQuery("");
            setSelectedIndex(0);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [isOpen]);

    useEffect(() => {
        setSelectedIndex(0);
    }, [query]);

    useEffect(() => {
        const handleKeyDown = (e: KeyboardEvent) => {
            if (!isOpen) return;

            if (e.key === "ArrowDown") {
                e.preventDefault();
                setSelectedIndex((i) => (i + 1) % filteredCommands.length);
            } else if (e.key === "ArrowUp") {
                e.preventDefault();
                setSelectedIndex((i) => (i - 1 + filteredCommands.length) % filteredCommands.length);
            } else if (e.key === "Enter") {
                e.preventDefault();
                if (filteredCommands[selectedIndex]) {
                    filteredCommands[selectedIndex].action();
                    onClose();
                }
            } else if (e.key === "Escape") {
                e.preventDefault();
                onClose();
            }
        };

        window.addEventListener("keydown", handleKeyDown);
        return () => window.removeEventListener("keydown", handleKeyDown);
    }, [isOpen, selectedIndex, filteredCommands, onClose]);

    if (!isOpen) return null;

    const categoryLabels = {
        page: "P├íginas",
        project: "Proyectos",
        workspace: "Workspaces",
        action: "Acciones",
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <>
                    {/* Backdrop */}
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        onClick={onClose}
                        style={{
                            position: "fixed",
                            inset: 0,
                            background: "rgba(0, 0, 0, 0.5)",
                            zIndex: 9998,
                        }}
                    />

                    {/* Command Palette */}
                    <motion.div
                        initial={{ opacity: 0, scale: 0.95, y: -20 }}
                        animate={{ opacity: 1, scale: 1, y: 0 }}
                        exit={{ opacity: 0, scale: 0.95, y: -20 }}
                        transition={{ duration: 0.15 }}
                        style={{
                            position: "fixed",
                            top: "20%",
                            left: "50%",
                            transform: "translateX(-50%)",
                            width: "90%",
                            maxWidth: "600px",
                            background: theme.colors.background.secondary,
                            borderRadius: theme.borderRadius.lg,
                            boxShadow: "0 20px 60px rgba(0, 0, 0, 0.3)",
                            zIndex: 9999,
                            overflow: "hidden",
                        }}
                    >
                        {/* Search Input */}
                        <div style={{ padding: theme.spacing.lg, borderBottom: `1px solid ${theme.colors.border.subtle}` }}>
                            <input
                                type="text"
                                value={query}
                                onChange={(e) => setQuery(e.target.value)}
                                placeholder="Buscar p├íginas, proyectos, acciones..."
                                autoFocus
                                style={{
                                    width: "100%",
                                    padding: `${theme.spacing.md} 0`,
                                    border: "none",
                                    outline: "none",
                                    background: "transparent",
                                    fontSize: theme.typography.fontSize.lg,
                                    color: theme.colors.text.primary,
                                    fontFamily: theme.typography.fontFamily.primary,
                                }}
                            />
                        </div>

                        {/* Results */}
                        <div style={{ maxHeight: "400px", overflowY: "auto" }}>
                            {filteredCommands.length === 0 ? (
                                <div style={{
                                    padding: theme.spacing.xl,
                                    textAlign: "center",
                                    color: theme.colors.text.secondary,
                                }}>
                                    No se encontraron resultados
                                </div>
                            ) : (
                                filteredCommands.map((cmd, index) => (
                                    <div
                                        key={cmd.id}
                                        onClick={() => {
                                            cmd.action();
                                            onClose();
                                        }}
                                        style={{
                                            padding: `${theme.spacing.md} ${theme.spacing.lg}`,
                                            background: index === selectedIndex ? theme.colors.background.tertiary : "transparent",
                                            cursor: "pointer",
                                            display: "flex",
                                            alignItems: "center",
                                            gap: theme.spacing.md,
                                            transition: `background ${theme.transitions.fast}`,
                                        }}
                                        onMouseEnter={() => setSelectedIndex(index)}
                                    >
                                        <span style={{ fontSize: "24px" }}>{cmd.icon}</span>
                                        <div style={{ flex: 1 }}>
                                            <div style={{
                                                fontSize: theme.typography.fontSize.base,
                                                fontWeight: theme.typography.fontWeight.medium,
                                                color: theme.colors.text.primary,
                                            }}>
                                                {cmd.label}
                                            </div>
                                            <div style={{
                                                fontSize: theme.typography.fontSize.xs,
                                                color: theme.colors.text.tertiary,
                                            }}>
                                                {categoryLabels[cmd.category]}
                                            </div>
                                        </div>
                                        {index === selectedIndex && (
                                            <span style={{
                                                fontSize: theme.typography.fontSize.xs,
                                                color: theme.colors.text.tertiary,
                                            }}>
                                                ÔåÁ
                                            </span>
                                        )}
                                    </div>
                                ))
                            )}
                        </div>

                        {/* Footer */}
                        <div style={{
                            padding: theme.spacing.sm,
                            borderTop: `1px solid ${theme.colors.border.subtle}`,
                            display: "flex",
                            gap: theme.spacing.lg,
                            fontSize: theme.typography.fontSize.xs,
                            color: theme.colors.text.tertiary,
                        }}>
                            <span>ÔåæÔåô Navegar</span>
                            <span>ÔåÁ Seleccionar</span>
                            <span>ESC Cerrar</span>
                        </div>
                    </motion.div>
                </>
            )}
        </AnimatePresence>
    );
}

import { Component, ReactNode } from "react";
import { theme } from "../styles/theme";
import { errorHandler, AppError } from "../utils/errorHandler";

interface Props {
    children: ReactNode;
    fallback?: ReactNode;
}

interface State {
    hasError: boolean;
    error?: AppError;
}

export default class ErrorBoundary extends Component<Props, State> {
    constructor(props: Props) {
        super(props);
        this.state = { hasError: false };
    }

    static getDerivedStateFromError(error: Error): State {
        return { hasError: true, error: error as AppError };
    }

    componentDidCatch(error: Error, errorInfo: any) {
        // Use centralized error handler
        errorHandler.handleError(error, "ErrorBoundary");
        console.error("React Error Info:", errorInfo);
    }

    render() {
        if (this.state.hasError && this.state.error) {
            if (this.props.fallback) {
                return this.props.fallback;
            }

            const userMessage = errorHandler.getUserMessage(this.state.error);
            const isRecoverable = this.state.error.isRecoverable;

            return (
                <div style={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    justifyContent: "center",
                    minHeight: "100vh",
                    padding: theme.spacing.xl,
                    background: theme.colors.background.primary,
                    textAlign: "center",
                }}>
                    <div style={{ fontSize: "64px", marginBottom: theme.spacing.lg }}>
                        {isRecoverable ? "­ƒôí" : "ÔÜá´©Å"}
                    </div>
                    <h1 style={{
                        margin: `0 0 ${theme.spacing.sm} 0`,
                        fontSize: theme.typography.fontSize["2xl"],
                        fontWeight: theme.typography.fontWeight.bold,
                        color: theme.colors.text.primary,
                    }}>
                        {isRecoverable ? "Problema de conexi├│n" : "Algo sali├│ mal"}
                    </h1>
                    <p style={{
                        margin: `0 0 ${theme.spacing.lg} 0`,
                        fontSize: theme.typography.fontSize.base,
                        color: theme.colors.text.secondary,
                        maxWidth: "400px",
                    }}>
                        {userMessage}
                    </p>
                    <div style={{ display: "flex", gap: theme.spacing.md }}>
                        <button
                            onClick={() => window.location.reload()}
                            style={{
                                padding: `${theme.spacing.md} ${theme.spacing.xl}`,
                                background: theme.colors.text.primary,
                                color: theme.colors.background.primary,
                                border: "none",
                                borderRadius: theme.borderRadius.md,
                                fontSize: theme.typography.fontSize.base,
                                fontWeight: theme.typography.fontWeight.medium,
                                cursor: "pointer",
                            }}
                        >
                            Recargar p├ígina
                        </button>
                        {isRecoverable && (
                            <button
                                onClick={() => this.setState({ hasError: false })}
                                style={{
                                    padding: `${theme.spacing.md} ${theme.spacing.xl}`,
                                    background: "transparent",
                                    border: `1px solid ${theme.colors.border.default}`,
                                    color: theme.colors.text.primary,
                                    borderRadius: theme.borderRadius.md,
                                    fontSize: theme.typography.fontSize.base,
                                    fontWeight: theme.typography.fontWeight.medium,
                                    cursor: "pointer",
                                }}
                            >
                                Reintentar
                            </button>
                        )}
                    </div>

                    {import.meta.env.DEV && this.state.error && (
                        <details style={{
                            marginTop: theme.spacing.xl,
                            padding: theme.spacing.md,
                            background: theme.colors.background.surface || theme.colors.background.secondary,
                            borderRadius: theme.borderRadius.sm,
                            fontSize: theme.typography.fontSize.xs,
                            color: theme.colors.text.tertiary,
                            textAlign: "left",
                            maxWidth: "600px",
                            width: "100%",
                            overflow: "auto",
                        }}>
                            <summary style={{ cursor: "pointer", marginBottom: theme.spacing.sm }}>
                                Detalles t├®cnicos ({this.state.error.type})
                            </summary>
                            <pre style={{ margin: 0, whiteSpace: "pre-wrap", wordBreak: "break-word" }}>
                                {this.state.error.message}
                                {"\n\n"}
                                {this.state.error.stack}
                            </pre>
                        </details>
                    )}
                </div>
            );
        }

        return this.props.children;
    }
}

import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import { useState } from "react";
import { theme } from "../styles/theme";

interface MarkdownRendererProps {
    content: string;
}

export default function MarkdownRenderer({ content }: MarkdownRendererProps) {
    const [copiedCode, setCopiedCode] = useState<string | null>(null);

    const copyToClipboard = (code: string, index: number) => {
        navigator.clipboard.writeText(code);
        setCopiedCode(`code-${index}`);
        setTimeout(() => setCopiedCode(null), 2000);
    };

    let codeBlockIndex = 0;

    return (
        <ReactMarkdown
            remarkPlugins={[remarkGfm]}
            components={{
                code({ node, inline, className, children, ...props }) {
                    const match = /language-(\w+)/.exec(className || "");
                    const codeString = String(children).replace(/\n$/, "");
                    const currentIndex = codeBlockIndex++;

                    if (!inline && match) {
                        return (
                            <div style={{
                                position: "relative",
                                marginBottom: theme.spacing.md,
                            }}>
                                <div style={{
                                    display: "flex",
                                    justifyContent: "space-between",
                                    alignItems: "center",
                                    padding: `${theme.spacing.xs} ${theme.spacing.sm}`,
                                    background: "#1E293B",
                                    borderTopLeftRadius: theme.borderRadius.sm,
                                    borderTopRightRadius: theme.borderRadius.sm,
                                    fontSize: theme.typography.fontSize.xs,
                                    color: "#94A3B8",
                                }}>
                                    <span>{match[1]}</span>
                                    <button
                                        onClick={() => copyToClipboard(codeString, currentIndex)}
                                        style={{
                                            background: copiedCode === `code-${currentIndex}` ? "#10B981" : "transparent",
                                            border: "1px solid #475569",
                                            borderRadius: theme.borderRadius.sm,
                                            padding: `${theme.spacing.xs} ${theme.spacing.sm}`,
                                            color: "#FFFFFF",
                                            fontSize: theme.typography.fontSize.xs,
                                            cursor: "pointer",
                                            transition: "all 0.2s",
                                        }}
                                    >
                                        {copiedCode === `code-${currentIndex}` ? "Ô£ô Copiado" : "Copiar"}
                                    </button>
                                </div>
                                <pre style={{
                                    margin: 0,
                                    padding: theme.spacing.md,
                                    background: "#0F172A",
                                    borderBottomLeftRadius: theme.borderRadius.sm,
                                    borderBottomRightRadius: theme.borderRadius.sm,
                                    overflowX: "auto",
                                }}>
                                    <code
                                        className={className}
                                        {...props}
                                        style={{
                                            fontFamily: "Monaco, Consolas, monospace",
                                            fontSize: theme.typography.fontSize.sm,
                                            color: "#E2E8F0",
                                        }}
                                    >
                                        {children}
                                    </code>
                                </pre>
                            </div>
                        );
                    }

                    return (
                        <code
                            className={className}
                            {...props}
                            style={{
                                background: "rgba(0, 0, 0, 0.05)",
                                padding: "2px 6px",
                                borderRadius: "3px",
                                fontFamily: "Monaco, Consolas, monospace",
                                fontSize: "0.9em",
                            }}
                        >
                            {children}
                        </code>
                    );
                },
                a({ node, children, ...props }) {
                    return (
                        <a
                            {...props}
                            target="_blank"
                            rel="noopener noreferrer"
                            style={{
                                color: theme.colors.accent.primary,
                                textDecoration: "underline",
                            }}
                        >
                            {children}
                        </a>
                    );
                },
                table({ node, children, ...props }) {
                    return (
                        <div style={{ overflowX: "auto", marginBottom: theme.spacing.md }}>
                            <table
                                {...props}
                                style={{
                                    width: "100%",
                                    borderCollapse: "collapse",
                                    fontSize: theme.typography.fontSize.sm,
                                }}
                            >
                                {children}
                            </table>
                        </div>
                    );
                },
                th({ node, children, ...props }) {
                    return (
                        <th
                            {...props}
                            style={{
                                padding: theme.spacing.sm,
                                borderBottom: `2px solid ${theme.colors.border.default}`,
                                textAlign: "left",
                                fontWeight: theme.typography.fontWeight.semibold,
                                background: theme.colors.background.secondary,
                            }}
                        >
                            {children}
                        </th>
                    );
                },
                td({ node, children, ...props }) {
                    return (
                        <td
                            {...props}
                            style={{
                                padding: theme.spacing.sm,
                                borderBottom: `1px solid ${theme.colors.border.subtle}`,
                            }}
                        >
                            {children}
                        </td>
                    );
                },
                ul({ node, children, ...props }) {
                    return (
                        <ul
                            {...props}
                            style={{
                                paddingLeft: theme.spacing.xl,
                                marginBottom: theme.spacing.md,
                            }}
                        >
                            {children}
                        </ul>
                    );
                },
                ol({ node, children, ...props }) {
                    return (
                        <ol
                            {...props}
                            style={{
                                paddingLeft: theme.spacing.xl,
                                marginBottom: theme.spacing.md,
                            }}
                        >
                            {children}
                        </ol>
                    );
                },
                blockquote({ node, children, ...props }) {
                    return (
                        <blockquote
                            {...props}
                            style={{
                                borderLeft: `4px solid ${theme.colors.accent.primary}`,
                                paddingLeft: theme.spacing.md,
                                marginLeft: 0,
                                marginBottom: theme.spacing.md,
                                color: theme.colors.text.secondary,
                                fontStyle: "italic",
                            }}
                        >
                            {children}
                        </blockquote>
                    );
                },
            }}
        >
            {content}
        </ReactMarkdown>
    );
}

import { useState, useEffect } from "react";
import Modal from "./Modal";
import Input from "./Input";
import Button from "./Button";
import { theme } from "../styles/theme";

interface RenameRunModalProps {
  isOpen: boolean;
  onClose: () => void;
  onRename: (name: string) => Promise<void>;
  currentName?: string;
  isLoading?: boolean;
}

export default function RenameRunModal({
  isOpen,
  onClose,
  onRename,
  currentName = "",
  isLoading = false,
}: RenameRunModalProps) {
  const [name, setName] = useState(currentName);

  useEffect(() => {
    if (isOpen) {
      setName(currentName);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isOpen, currentName]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const trimmedName = name.trim();
    if (!trimmedName) {
      return;
    }

    try {
      await onRename(trimmedName);
      onClose();
    } catch (err) {
      console.error("Rename error:", err);
    }
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} title="Rename Run">
      <form onSubmit={handleSubmit}>
        <Input
          type="text"
          value={name}
          onChange={setName}
          label="Run Name"
          placeholder="Enter a descriptive name..."
          maxLength={100}
          required
          autoFocus
        />

        <div style={{
          display: "flex",
          gap: theme.spacing.md,
          justifyContent: "flex-end",
          marginTop: theme.spacing.lg,
        }}>
          <Button
            type="button"
            onClick={onClose}
            variant="ghost"
            disabled={isLoading}
          >
            Cancel
          </Button>
          <Button
            type="submit"
            disabled={isLoading || !name.trim()}
          >
            {isLoading ? "Saving..." : "Save"}
          </Button>
        </div>
      </form>
    </Modal>
  );
}

import { useState } from "react";
import Modal from "./Modal";
import Button from "./Button";
import Input from "./Input";
import { theme } from "../styles/theme";
import { api } from "../config/api";

interface ShareModalProps {
  isOpen: boolean;
  onClose: () => void;
  runId?: string;
  sessionId?: string;
}

export default function ShareModal({ isOpen, onClose, runId, sessionId }: ShareModalProps) {
  const [expiresInDays, setExpiresInDays] = useState("");
  const [password, setPassword] = useState("");
  const [maxViews, setMaxViews] = useState("");
  const [isCreating, setIsCreating] = useState(false);
  const [shareLink, setShareLink] = useState<string | null>(null);

  const handleCreate = async () => {
    setIsCreating(true);

    try {
      const response = await api.post<{ data: { token: string } }>("/api/shares", {
        run_id: runId || null,
        session_id: sessionId || null,
        expires_in_days: expiresInDays ? parseInt(expiresInDays) : null,
        password: password || null,
        max_views: maxViews ? parseInt(maxViews) : null,
      });

      const token = (response as any).data.token;
      const url = `${window.location.origin}/share/${token}`;
      setShareLink(url);
    } catch (error) {
      console.error("Error creating share link:", error);
      alert("Failed to create share link. Please try again.");
    } finally {
      setIsCreating(false);
    }
  };

  const handleCopyLink = () => {
    if (shareLink) {
      navigator.clipboard.writeText(shareLink);
      alert("Link copied to clipboard!");
    }
  };

  const handleClose = () => {
    setShareLink(null);
    setExpiresInDays("");
    setPassword("");
    setMaxViews("");
    onClose();
  };

  return (
    <Modal isOpen={isOpen} onClose={handleClose} title="Share">
      {!shareLink ? (
        <div>
          <p style={{
            margin: `0 0 ${theme.spacing.lg} 0`,
            color: theme.colors.text.secondary,
            fontSize: theme.typography.fontSize.body,
            fontFamily: theme.typography.fontFamily.primary,
          }}>
            Configure share link settings
          </p>

          <div style={{ marginBottom: theme.spacing.lg }}>
            <Input
              type="number"
              value={expiresInDays}
              onChange={setExpiresInDays}
              label="Expires in (days)"
              placeholder="Leave empty for no expiration"
            />
          </div>

          <div style={{ marginBottom: theme.spacing.lg }}>
            <Input
              type="password"
              value={password}
              onChange={setPassword}
              label="Password (optional)"
              placeholder="Leave empty for public access"
            />
          </div>

          <div style={{ marginBottom: theme.spacing.xl }}>
            <Input
              type="number"
              value={maxViews}
              onChange={setMaxViews}
              label="Max views (optional)"
              placeholder="Leave empty for unlimited views"
            />
          </div>

          <div style={{
            display: 'flex',
            gap: theme.spacing.md,
            justifyContent: 'flex-end',
          }}>
            <Button
              type="button"
              onClick={handleClose}
              variant="ghost"
              disabled={isCreating}
            >
              Cancel
            </Button>
            <Button 
              type="button" 
              onClick={handleCreate}
              disabled={isCreating}
            >
              {isCreating ? 'Creating...' : 'Create Link'}
            </Button>
          </div>
        </div>
      ) : (
        <div>
          <p style={{
            margin: `0 0 ${theme.spacing.md} 0`,
            color: theme.colors.text.secondary,
            fontSize: theme.typography.fontSize.body,
            fontFamily: theme.typography.fontFamily.primary,
          }}>
            Share link created successfully!
          </p>

          <div style={{
            padding: theme.spacing.md,
            background: theme.colors.background.tertiary,
            border: `1px solid ${theme.colors.border.accent}`,
            borderRadius: theme.borderRadius.medium,
            marginBottom: theme.spacing.lg,
            fontFamily: theme.typography.fontFamily.mono,
            fontSize: theme.typography.fontSize.bodySmall,
            color: theme.colors.accent.primary,
            wordBreak: 'break-all',
          }}>
            {shareLink}
          </div>

          <div style={{
            display: 'flex',
            gap: theme.spacing.md,
            justifyContent: 'flex-end',
          }}>
            <Button
              type="button"
              onClick={handleClose}
              variant="ghost"
            >
              Close
            </Button>
            <Button 
              type="button" 
              onClick={handleCopyLink}
            >
              Copy Link
            </Button>
