import { create } from "zustand";
import { persist } from "zustand/middleware";
import { supabase } from "../config/supabase";
import type { User, Session } from "@supabase/supabase-js";

interface AuthState {
  user: User | null;
  session: Session | null;
  loading: boolean;

  // Actions
  signIn: (email: string, password: string, rememberMe?: boolean) => Promise<void>;
  signUp: (email: string, password: string, displayName: string) => Promise<void>;
  signOut: () => Promise<void>;
  initialize: () => Promise<void>;
  resetPassword: (email: string) => Promise<void>;
}

export const useAuthStore = create<AuthState>()(
  persist(
    (set) => ({
      user: null,
      session: null,
      loading: true,

      signIn: async (email: string, password: string, _rememberMe: boolean = true) => {
        // Clear any existing session first
        await supabase.auth.signOut();

        // Sign in with password
        const { data, error } = await supabase.auth.signInWithPassword({
          email,
          password,
        });

        if (error) throw error;

        set({ user: data.user, session: data.session });
      },

      signUp: async (email: string, password: string, displayName: string) => {
        // Create auth user with metadata
        const { data, error } = await supabase.auth.signUp({
          email,
          password,
          options: {
            data: {
              full_name: displayName,
            },
          },
        });

        if (error) throw error;
        if (!data.user) throw new Error("User creation failed");

        // Profile creation is handled by the database trigger 'handle_new_user'
        // We just update the local state
        set({ user: data.user, session: data.session });
      },

      signOut: async () => {
        const { error } = await supabase.auth.signOut();
        if (error) throw error;

        set({ user: null, session: null });
      },

      resetPassword: async (email: string) => {
        const { error } = await supabase.auth.resetPasswordForEmail(email, {
          redirectTo: `${window.location.origin}/reset-password`,
        });

        if (error) throw error;
      },

      initialize: async () => {
        try {
          // Get current session
          const { data: { session } } = await supabase.auth.getSession();

          set({
            user: session?.user || null,
            session: session,
            loading: false,
          });

          // Listen for auth changes
          supabase.auth.onAuthStateChange((_event, session) => {
            set({
              user: session?.user || null,
              session: session,
            });
          });
        } catch (error) {
          console.error("Auth initialization error:", error);
          set({ loading: false });
        }
      },
    }),
    {
      name: "wadi-auth-storage",
      partialize: (state) => ({
        user: state.user,
        session: state.session,
      }),
    }
  )
);

import { create } from "zustand";
import { persist } from "zustand/middleware";
import { BillingPlan, UserUsage, BILLING_PLANS } from "../types/billing";

interface BillingState {
  currentPlan: BillingPlan;
  usage: UserUsage | null;
  loading: boolean;

  loadUsage: () => Promise<void>;
  checkLimit: (type: "message" | "file") => boolean;
  recordUsage: (type: "message" | "file_upload") => Promise<void>;
  upgradePlan: (planId: string) => Promise<void>;
}

export const useBillingStore = create<BillingState>()(
  persist(
    (set, get) => ({
      currentPlan: BILLING_PLANS.free,
      usage: null,
      loading: false,

      loadUsage: async () => {
        try {
          set({ loading: true });
          const token = localStorage.getItem("wadi_token");
          // Mock response if API is not ready
          if (!token) {
            set({ loading: false });
            return;
          }

          try {
            const response = await fetch(`${import.meta.env.VITE_API_URL}/api/billing/usage`, {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            });

            if (response.ok) {
              const data = await response.json();
              const usage = data.data.usage;
              const plan = BILLING_PLANS[usage.planId] || BILLING_PLANS.free;

              set({
                usage,
                currentPlan: plan,
                loading: false
              });
            } else {
              // Fallback for dev/demo
              set({ loading: false });
            }
          } catch (e) {
            // Fallback if endpoint doesn't exist yet
            set({ loading: false });
          }
        } catch (error) {
          console.error("Error loading usage:", error);
          set({ loading: false });
        }
      },

      checkLimit: (type) => {
        const { usage, currentPlan } = get();
        // If no usage data, assume free limits for safety or allow if dev
        if (!usage) return true;

        const now = new Date();
        const periodEnd = new Date(usage.currentPeriodEnd);

        // Reset if period ended
        if (now > periodEnd) {
          return true; // Allow, will be reset on backend
        }

        if (type === "message") {
          return usage.dailyMessagesUsed < currentPlan.limits.dailyMessages &&
            usage.messagesUsed < currentPlan.limits.monthlyMessages;
        }

        return true;
      },

      recordUsage: async (type) => {
        try {
          const token = localStorage.getItem("wadi_token");
          if (!token) return;

          await fetch(`${import.meta.env.VITE_API_URL}/api/billing/usage`, {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ type }),
          }).catch(() => { }); // Ignore errors for now

          // Reload usage after recording
          // await get().loadUsage(); // Optimistic update might be better
        } catch (error) {
          console.error("Error recording usage:", error);
        }
      },

      upgradePlan: async (planId) => {
        try {
          set({ loading: true });
          const token = localStorage.getItem("wadi_token");
          const response = await fetch(`${import.meta.env.VITE_API_URL}/api/billing/upgrade`, {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ planId }),
          });

          if (response.ok) {
            const data = await response.json();
            // If Stripe checkout URL is returned, redirect
            if (data.data.checkoutUrl) {
              window.location.href = data.data.checkoutUrl;
            } else {
              await get().loadUsage();
            }
          }
          set({ loading: false });
        } catch (error) {
          console.error("Error upgrading plan:", error);
          set({ loading: false });
        }
      },
    }),
    {
      name: "wadi-billing-storage",
      partialize: (state) => ({
        currentPlan: state.currentPlan,
        usage: state.usage,
      }),
    }
  )
);

import { create } from "zustand";
import { persist } from "zustand/middleware";
import { api } from "../config/api";
import { supabase } from "../config/supabase";
import type { Message, Conversation, WebSocketMessage } from "../types/chat";


interface ChatState {
  // Current conversation
  currentConversationId: string | null;
  currentConversation: Conversation | null;
  messages: Message[];

  // All conversations
  conversations: Conversation[];

  // Loading states
  loadingMessages: boolean;
  loadingConversations: boolean;
  sendingMessage: boolean;

  // WebSocket
  socket: WebSocket | null;
  isConnected: boolean;

  // Error state
  error: string | null;

  // Actions
  connect: (conversationId: string) => void;
  disconnect: () => void;
  sendMessage: (message: string) => Promise<void>;
  loadConversation: (conversationId: string) => Promise<void>;
  loadConversations: () => Promise<void>;
  deleteConversation: (conversationId: string) => Promise<void>;
  clearCurrentConversation: () => void;
  clearError: () => void;
}

export const useChatStore = create<ChatState>()((set, get) => ({
  // Initial state
  currentConversationId: null,
  currentConversation: null,
  messages: [],
  conversations: [],
  loadingMessages: false,
  loadingConversations: false,
  sendingMessage: false,
  socket: null,
  isConnected: false,
  error: null,

  // Connect to WebSocket
  connect: async (conversationId: string) => {
    const { socket } = get();
    if (socket) {
      socket.close();
    }

    const wsUrl = import.meta.env.VITE_API_URL.replace("http", "ws") + `/ws/chat/${conversationId}`;
    const ws = new WebSocket(wsUrl);

    ws.onopen = async () => {
      console.log("[ChatStore] WebSocket connected");
      set({ isConnected: true });

      // Authenticate
      const { data: { session } } = await supabase.auth.getSession();
      if (session?.access_token) {
        ws.send(JSON.stringify({ type: "auth", token: session.access_token }));
      }
    };

    ws.onmessage = (event) => {
      try {
        const data: WebSocketMessage = JSON.parse(event.data);

        switch (data.type) {
          case "start":
            set((state) => ({
              messages: [
                ...state.messages,
                {
                  id: "temp-ai-" + Date.now(),
                  conversation_id: conversationId,
                  role: "assistant",
                  content: "",
                  created_at: new Date().toISOString(),
                },
              ],
            }));
            break;

          case "chunk":
            set((state) => {
              const newMessages = [...state.messages];
              const lastMessage = newMessages[newMessages.length - 1];
              if (lastMessage && lastMessage.role === "assistant") {
                lastMessage.content += data.content;
              }
              return { messages: newMessages };
            });
            break;

          case "complete":
            set({ sendingMessage: false });
            // Reload conversations to update counts/last message
            get().loadConversations();
            break;

          case "error":
            console.error("[ChatStore] WS Error:", data.message);
            set({ error: data.message, sendingMessage: false });
            break;
        }
      } catch (error) {
        console.error("[ChatStore] Error parsing WS message:", error);
      }
    };

    ws.onclose = () => {
      console.log("[ChatStore] WebSocket disconnected");
      set({ isConnected: false, socket: null });
    };

    set({ socket: ws });
  },

  disconnect: () => {
    const { socket } = get();
    if (socket) {
      socket.close();
      set({ socket: null, isConnected: false });
    }
  },

  // Send a message
  sendMessage: async (message: string) => {
    const { socket, currentConversationId, connect } = get();

    if (!message.trim()) return;

    set({ sendingMessage: true, error: null });

    // Optimistic update for user message
    const tempId = "temp-user-" + Date.now();
    set((state) => ({
      messages: [
        ...state.messages,
        {
          id: tempId,
          conversation_id: currentConversationId || "",
          role: "user",
          content: message,
          created_at: new Date().toISOString(),
        },
      ],
    }));

    // If no conversation, create one first via REST (or handle in WS if we want to support new convos via WS)
    // For now, let's assume we need a conversation ID to connect WS.
    // If currentConversationId is null, we might need to create it first.
    let targetConversationId = currentConversationId;

    if (!targetConversationId) {
      try {
        // Create conversation via API
        const { data } = await api.post<{ ok: boolean, data: any }>("/api/chat", { message }); // This endpoint might need adjustment if we only want to create convo
        // Actually, the current sendMessage endpoint does everything. 
        // Let's stick to the plan: Use WS for streaming.
        // But we need a conversation ID to connect to /ws/chat/:id

        // So if no conversation, we call the API once to create it and get the first response (non-streaming or streaming if we change API).
        // To keep it simple: If no conversation, call API. If conversation exists, use WS.

        // Wait, the plan said "Adapt to handle conversationId".
        // If we don't have an ID, we can't connect to the specific channel.

        // Strategy:
        // 1. If no conversationId, call API to create one and send first message.
        // 2. Then connect WS for subsequent messages.

        const response = await api.post<{ ok: boolean, data: { conversationId: string, userMessage: Message, assistantMessage: Message } }>("/api/chat", {
          message,
        });

        if (response.ok) {
          const { conversationId, userMessage, assistantMessage } = response.data;
          set({
            currentConversationId: conversationId,
            messages: [userMessage, assistantMessage],
            sendingMessage: false
          });
          get().loadConversations();
          connect(conversationId); // Connect for next messages
          return;
        }
      } catch (error) {
        console.error("Error creating conversation:", error);
        set({ error: "Failed to start conversation", sendingMessage: false });
        return;
      }
    }

    // If we have a socket and it's open, send via WS
    if (socket && socket.readyState === WebSocket.OPEN) {
      socket.send(JSON.stringify({ type: "message", content: message }));
    } else {
      // Reconnect and send? Or fallback to API?
      // Let's try to reconnect if we have an ID
      if (targetConversationId) {
        connect(targetConversationId);
        // Wait a bit for connection? This is tricky without a queue.
        // For now, let's show an error if not connected.
        set({ error: "Connection lost. Please try again.", sendingMessage: false });
      }
    }
  },

  // Load a specific conversation with all messages
  loadConversation: async (conversationId: string) => {
    set({ loadingMessages: true, error: null });

    try {
      const response = await api.get<{
        ok: boolean;
        data: {
          conversation: Conversation;
          messages: Message[];
        };
      }>(`/api/chat/${conversationId}`);

      if (!response.ok) {
        throw new Error("Failed to load conversation");
      }

      const { conversation, messages } = response.data;

      set({
        currentConversationId: conversationId,
        currentConversation: conversation,
        messages,
        loadingMessages: false,
      });

      // Connect WS
      get().connect(conversationId);

    } catch (error: any) {
      console.error("Error loading conversation:", error);
      set({
        loadingMessages: false,
        error: error.error || error.message || "No pude cargar la conversaci├│n.",
      });
    }
  },

  // Load all conversations
  loadConversations: async () => {
    set({ loadingConversations: true, error: null });

    try {
      const response = await api.get<{
        ok: boolean;
        data: Conversation[];
      }>("/api/chat");

      if (!response.ok) {
        throw new Error("Failed to load conversations");
      }

      set({
        conversations: response.data,
        loadingConversations: false,
      });
    } catch (error: any) {
      console.error("Error loading conversations:", error);
      set({
        loadingConversations: false,
        error: error.error || error.message || "No pude cargar las conversaciones.",
      });
    }
  },

  // Delete a conversation
  deleteConversation: async (conversationId: string) => {
    try {
      await api.delete(`/api/chat/${conversationId}`);

      set((state) => ({
        conversations: state.conversations.filter((c) => c.id !== conversationId),
      }));

      const { currentConversationId } = get();
      if (currentConversationId === conversationId) {
        get().disconnect();
        set({
          currentConversationId: null,
          currentConversation: null,
          messages: [],
        });
      }
    } catch (error: any) {
      console.error("Error deleting conversation:", error);
      set({
        error: error.error || error.message || "No pude eliminar la conversaci├│n.",
      });
      throw error;
    }
  },

  // Clear current conversation
  clearCurrentConversation: () => {
    get().disconnect();
    set({
      currentConversationId: null,
      currentConversation: null,
      messages: [],
    });
  },

  // Clear error
  clearError: () => {
    set({ error: null });
  },
}));

import { create } from "zustand";

export interface AgentLog {
    id: string;
    timestamp: number;
    type: "prompt" | "response" | "action" | "error";
    content: any;
    metadata?: any;
}

interface DebugStore {
    logs: AgentLog[];
    maxLogs: number;

    addLog: (type: AgentLog["type"], content: any, metadata?: any) => void;
    clearLogs: () => void;
    getLogsByType: (type: AgentLog["type"]) => AgentLog[];
}

export const useDebugStore = create<DebugStore>((set, get) => ({
    logs: [],
    maxLogs: 100,

    addLog: (type, content, metadata) => {
        const log: AgentLog = {
            id: Math.random().toString(36).substring(7),
            timestamp: Date.now(),
            type,
            content,
            metadata,
        };

        set((state) => ({
            logs: [...state.logs.slice(-state.maxLogs + 1), log],
        }));
    },

    clearLogs: () => {
        set({ logs: [] });
    },

    getLogsByType: (type) => {
        return get().logs.filter((log) => log.type === type);
    },
}));

// Helper to log agent activity
export function logAgentPrompt(prompt: string, metadata?: any) {
    useDebugStore.getState().addLog("prompt", prompt, metadata);
}

export function logAgentResponse(response: any, metadata?: any) {
    useDebugStore.getState().addLog("response", response, metadata);
}

export function logAgentAction(action: any, metadata?: any) {
    useDebugStore.getState().addLog("action", action, metadata);
}

export function logAgentError(error: any, metadata?: any) {
    useDebugStore.getState().addLog("error", error, metadata);
}

