  fetchRuns: false,
  createRun: false,
  updateRun: false,
  deleteRun: false,
  renameRun: false,
  tagRun: false,
};

function createErrorState(operation: string, error: ApiError | Error, retryable: boolean = false): ErrorState {
  const message = 'error' in error ? error.error : error.message;
  return {
    operation,
    message,
    timestamp: Date.now(),
