export function getPlanLimits(planId: string): UsageLimits {
    return BILLING_PLANS[planId]?.limits || BILLING_PLANS.free.limits;
}

export function canUseFeature(
    usage: UserUsage,
    feature: keyof UsageLimits
): boolean {
    const limits = getPlanLimits(usage.planId);

    switch (feature) {
        case "monthlyMessages":
            return usage.messagesUsed < limits.monthlyMessages;
        case "dailyMessages":
            return usage.dailyMessagesUsed < limits.dailyMessages;
        default:
            return limits[feature] as boolean;
    }
}

import { logger } from './logger';
import { RunbookExecutor } from './runbooks';

export const ErrorType = {
    AUTH: 'AUTH',
    NETWORK: 'NETWORK',
    VALIDATION: 'VALIDATION',
    DATABASE: 'DATABASE',
    UNKNOWN: 'UNKNOWN',
} as const;

export type ErrorType = typeof ErrorType[keyof typeof ErrorType];

export interface AppError extends Error {
    type: ErrorType;
    originalError?: any;
    isRecoverable: boolean;
}

class ErrorHandler {
    public async handleError(error: any, context?: string): Promise<AppError> {
        const appError = this.classifyError(error);

        logger.error(
            `[${context || 'Global'}] ${appError.message}`,
            appError,
            { type: appError.type, original: error }
        );

        // Attempt to auto-heal
        const healed = await RunbookExecutor.execute(appError);
        if (healed) {
            logger.info(`[${context || 'Global'}] Error auto-healed by runbook.`);
        }

        return appError;
    }

    private classifyError(error: any): AppError {
        const message = error?.message || 'An unexpected error occurred';
        let type: ErrorType = ErrorType.UNKNOWN;
        let isRecoverable = false;

        // Auth Errors
        if (
            message.includes('auth') ||
            message.includes('JWT') ||
            message.includes('session') ||
            error?.status === 401 ||
            error?.status === 403
        ) {
            type = ErrorType.AUTH;
            isRecoverable = true; // Usually recoverable by re-login
        }

        // Network Errors
        else if (
            message.includes('fetch') ||
            message.includes('network') ||
            message.includes('connection')
        ) {
            type = ErrorType.NETWORK;
            isRecoverable = true; // Recoverable by retry
        }

        // Validation Errors
        else if (
            message.includes('validation') ||
            message.includes('invalid') ||
            error?.status === 400
        ) {
            type = ErrorType.VALIDATION;
            isRecoverable = false; // User needs to fix input
        }

        // Database Errors
        else if (
            message.includes('database') ||
            message.includes('duplicate key') ||
            message.includes('foreign key')
        ) {
            type = ErrorType.DATABASE;
            isRecoverable = false; // Usually indicates a bug or conflict
        }

        const appError = new Error(message) as AppError;
        appError.type = type;
        appError.originalError = error;
        appError.isRecoverable = isRecoverable;

        return appError;
    }

    public getUserMessage(error: AppError): string {
        switch (error.type) {
            case ErrorType.AUTH:
                return "Tu sesi├│n ha expirado. Por favor, inici├í sesi├│n nuevamente.";
            case ErrorType.NETWORK:
                return "Error de conexi├│n. Verific├í tu internet e intent├í de nuevo.";
            case ErrorType.VALIDATION:
                return "Por favor, revis├í los datos ingresados.";
            case ErrorType.DATABASE:
                return "Hubo un problema con el sistema. Por favor, contact├í a soporte.";
            default:
                return "Ocurri├│ un error inesperado. Estamos trabajando en ello.";
        }
    }
}

export const errorHandler = new ErrorHandler();

export type LogLevel = 'debug' | 'info' | 'warn' | 'error';

export interface LogEntry {
    level: LogLevel;
    message: string;
    timestamp: string;
    context?: Record<string, any>;
    error?: Error;
}

class Logger {
    private logs: LogEntry[] = [];
    private maxLogs = 100;

    private log(level: LogLevel, message: string, context?: Record<string, any>, error?: Error) {
        const entry: LogEntry = {
            level,
            message,
            timestamp: new Date().toISOString(),
            context,
            error,
        };

        // Add to internal buffer (for potential error reporting)
        this.logs.unshift(entry);
        if (this.logs.length > this.maxLogs) {
            this.logs.pop();
        }

        // Console output with styling
        const style = this.getStyle(level);
        console.groupCollapsed(`%c[${level.toUpperCase()}] ${message}`, style);
        if (context) console.log('Context:', context);
        if (error) console.error('Error:', error);
        console.log('Timestamp:', entry.timestamp);
        console.groupEnd();

        // TODO: Send to external logging service (e.g., Sentry, Datadog) in production
    }

    debug(message: string, context?: Record<string, any>) {
        if (import.meta.env.DEV) {
            this.log('debug', message, context);
        }
    }

    info(message: string, context?: Record<string, any>) {
        this.log('info', message, context);
    }

    warn(message: string, context?: Record<string, any>) {
        this.log('warn', message, context);
    }

    error(message: string, error?: Error, context?: Record<string, any>) {
        this.log('error', message, context, error);
    }

    getLogs() {
        return this.logs;
    }

    private getStyle(level: LogLevel): string {
        switch (level) {
            case 'debug': return 'color: #9CA3AF';
            case 'info': return 'color: #3B82F6';
            case 'warn': return 'color: #F59E0B';
            case 'error': return 'color: #EF4444; font-weight: bold';
        }
    }
}

export const logger = new Logger();

// API retry logic with exponential backoff

export interface RetryOptions {
    maxRetries?: number;
    initialDelay?: number;
    maxDelay?: number;
    backoffFactor?: number;
    retryableStatuses?: number[];
}

const defaultOptions: Required<RetryOptions> = {
    maxRetries: 3,
    initialDelay: 1000,
    maxDelay: 10000,
    backoffFactor: 2,
    retryableStatuses: [408, 429, 500, 502, 503, 504],
};

export async function fetchWithRetry<T>(
    url: string,
    options: RequestInit = {},
    retryOptions: RetryOptions = {}
): Promise<T> {
    const opts = { ...defaultOptions, ...retryOptions };
    let lastError: Error | null = null;

    for (let attempt = 0; attempt <= opts.maxRetries; attempt++) {
        try {
            const response = await fetch(url, options);

            // If response is OK, return parsed JSON
            if (response.ok) {
                return await response.json();
            }

            // Check if status is retryable
            if (!opts.retryableStatuses.includes(response.status)) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }

            // If it's the last attempt, throw
            if (attempt === opts.maxRetries) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }

            // Calculate delay with exponential backoff
            const delay = Math.min(
                opts.initialDelay * Math.pow(opts.backoffFactor, attempt),
                opts.maxDelay
            );

            console.warn(`Request failed, retrying in ${delay}ms... (attempt ${attempt + 1}/${opts.maxRetries})`);
            await new Promise((resolve) => setTimeout(resolve, delay));

        } catch (error) {
            lastError = error as Error;

            // If it's a network error and not the last attempt, retry
            if (attempt < opts.maxRetries) {
                const delay = Math.min(
                    opts.initialDelay * Math.pow(opts.backoffFactor, attempt),
                    opts.maxDelay
                );
                console.warn(`Request failed, retrying in ${delay}ms... (attempt ${attempt + 1}/${opts.maxRetries})`);
                await new Promise((resolve) => setTimeout(resolve, delay));
            }
        }
    }

    throw lastError || new Error("Request failed after retries");
}

// Wrapper for common API calls
export async function apiGet<T>(endpoint: string, retryOptions?: RetryOptions): Promise<T> {
    const token = localStorage.getItem("wadi_token");
    return fetchWithRetry<T>(
        `${import.meta.env.VITE_API_URL}${endpoint}`,
        {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                ...(token && { Authorization: `Bearer ${token}` }),
            },
        },
        retryOptions
    );
}

export async function apiPost<T>(
    endpoint: string,
    body: any,
    retryOptions?: RetryOptions
): Promise<T> {
    const token = localStorage.getItem("wadi_token");
    return fetchWithRetry<T>(
        `${import.meta.env.VITE_API_URL}${endpoint}`,
        {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                ...(token && { Authorization: `Bearer ${token}` }),
            },
            body: JSON.stringify(body),
        },
        retryOptions
    );
}
